import { createContext, useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { io } from 'socket.io-client';
import AuthContext from './AuthContext';

const ChatContext = createContext();

export const ChatProvider = ({ children }) => {
  const [messages, setMessages] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [typingUsers, setTypingUsers] = useState([]);
  const [socket, setSocket] = useState(null);
  const { user, isAuthenticated, token } = useContext(AuthContext);

  useEffect(() => {
    if (isAuthenticated && user && token) {
      const newSocket = io('http://localhost:5000', {
        path: '/socket.io/',
        withCredentials: true,
        extraHeaders: {
          'x-auth-token': token
        }
      });

      setSocket(newSocket);

      newSocket.on('connect', () => {
        newSocket.emit('register', user.id);
      });

      newSocket.on('message', (message) => {
        setMessages(prev => [...prev, message]);
      });

      newSocket.on('onlineUsers', (users) => {
        setOnlineUsers(users);
      });

      newSocket.on('typing', ({ userId, isTyping }) => {
        setTypingUsers(prev => 
          isTyping 
            ? [...new Set([...prev, userId])] 
            : prev.filter(id => id !== userId)
        );
      });

      return () => {
        newSocket.disconnect();
      };
    }
  }, [isAuthenticated, user, token]);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const res = await axios.get('/api/messages', {
          headers: {
            'x-auth-token': token
          }
        });
        setMessages(res.data);
      } catch (err) {
        console.error('Error fetching messages:', err);
      }
    };

    if (isAuthenticated && token) {
      fetchMessages();
    }
  }, [isAuthenticated, token]);

  const sendMessage = async (content) => {
    try {
      const res = await axios.post('/api/messages', { content }, {
        headers: {
          'x-auth-token': token
        }
      });
      socket.emit('message', res.data);
      return res.data;
    } catch (err) {
      console.error('Error sending message:', err);
      throw err.response?.data || { message: 'Failed to send message' };
    }
  };

  const sendTyping = (isTyping) => {
    if (socket && socket.connected) {
      socket.emit('typing', { userId: user.id, isTyping });
    }
  };

  return (
    <ChatContext.Provider value={{ 
      messages, 
      onlineUsers, 
      typingUsers, 
      sendMessage, 
      sendTyping 
    }}>
      {children}
    </ChatContext.Provider>
  );
};

export default ChatContext;
